                    |checkPositionOnBoard board (sel1 pos +1, sel2 pos) == True && checkPositionForPiece (sel1 pos +1, sel2 pos) board == True && getPositionColor board (sel1 pos +1, sel2 pos) == White && checkPositionOnBoard board (sel1 pos -2, sel2 pos) == True && checkPositionForPiece (sel1 pos -2, sel2 pos) board == True && getPositionColor board (sel1 pos -2, sel2 pos) == White && checkPositionOnBoard board (sel1 pos -1, sel2 pos) == True && checkPositionForPiece(sel1 pos -1, sel2 pos) board == True && getPositionColor board (sel1 pos -1, sel2 pos) == White && checkPositionOnBoard board pos == True && checkPositionForPiece pos board == True && getPositionColor board pos == White = False
